'create or replace PACKAGE BODY ObslugaUzytkownik IS',
'',
'  PROCEDURE DodajUzytkownika( ',
'      imieU VARCHAR2, ',
'      emailU VARCHAR2, ',
'      hasloU VARCHAR2, ',
'      data_urodzeniaU DATE, ',
'      typ_uzytkownikaU VARCHAR2 ',
') IS ',
'      brak_imienia EXCEPTION; ',
'      brak_emailu EXCEPTION; ',
'      brak_hasla EXCEPTION; ',
'      brak_typu_uzytkownika EXCEPTION; ',
'      max_id_uzytkownika NUMBER; ',
'BEGIN ',
unistr('    -- Tworzenie nowej kolekcji, je\015Bli jeszcze nie istnieje'),
'    IF NOT APEX_COLLECTION.COLLECTION_EXISTS(''KOLEKCJA_UZYTKOWNIKOW'') THEN',
'        APEX_COLLECTION.CREATE_COLLECTION(''KOLEKCJA_UZYTKOWNIKOW'');',
'    END IF;',
'',
unistr('    -- Walidacja danych wej\015Bciowych '),
'    IF imieU IS NULL OR obsluga_liczby(imieU) THEN ',
'        RAISE brak_imienia; ',
'    ELSIF emailU IS NULL OR NOT obsluga_symbole(emailU,''@'') THEN ',
'        RAISE brak_emailu; ',
'    ELSIF hasloU IS NULL THEN ',
'        RAISE brak_hasla; ',
'    ELSIF typ_uzytkownikaU NOT IN (''REGULAR'', ''PREMIUM'', ''FAMILY'') THEN ',
'        RAISE brak_typu_uzytkownika; ',
'    ELSE ',
unistr('        -- Generowanie nowego ID u\017Cytkownika '),
'        SELECT NVL(MAX(id_uzytkownik), 0) + 1 INTO max_id_uzytkownika ',
'        FROM Uzytkownik; ',
'',
unistr('        -- Dodanie danych u\017Cytkownika do kolekcji APEX'),
'        APEX_COLLECTION.ADD_MEMBER(',
'            p_collection_name => ''KOLEKCJA_UZYTKOWNIKOW'',',
'            p_c001 => max_id_uzytkownika,',
'            p_c002 => imieU,',
'            p_c003 => emailU,',
'            p_c004 => hasloU,',
'            p_c005 => TO_CHAR(data_urodzeniaU, ''YYYY-MM-DD''),',
'            p_c006 => typ_uzytkownikaU',
'        );',
'',
unistr('        -- Komunikat do log\00F3w'),
unistr('        DBMS_OUTPUT.PUT_LINE(''Dane u\017Cytkownika zapisano do kolekcji KOLEKCJA_UZYTKOWNIKOW.'');'),
'    END IF; ',
'',
'EXCEPTION ',
'    WHEN brak_imienia THEN ',
unistr('        DBMS_OUTPUT.PUT_LINE(''Nie wprowadzono imienia lub zawiera niedozwolone znaki. Uzupe\0142nij dane.''); '),
'    WHEN brak_emailu THEN ',
unistr('        DBMS_OUTPUT.PUT_LINE(''Nie wprowadzono adresu e-mail lub jest on nieprawid\0142owy. Uzupe\0142nij dane.''); '),
'    WHEN brak_hasla THEN ',
unistr('        DBMS_OUTPUT.PUT_LINE(''Nie wprowadzono has\0142a lub jest ono nieprawid\0142owe. Uzupe\0142nij dane.''); '),
'    WHEN brak_typu_uzytkownika THEN ',
unistr('        DBMS_OUTPUT.PUT_LINE(''Nie wprowadzono poprawnego typu subskrypcji. Dozwolone warto\015Bci: REGULAR, PREMIUM, FAMILY.''); '),
'    WHEN OTHERS THEN ',
unistr('        DBMS_OUTPUT.PUT_LINE(''Wyst\0105pi\0142 nieoczekiwany b\0142\0105d podczas dodawania u\017Cytkownika.''); '),
'END DodajUzytkownika;',
'',
'',
'  PROCEDURE UsunUzytkownika(',
'    id_uzytkownikU IN INTEGER',
') IS',
'BEGIN',
unistr('    -- Usuni\0119cie powi\0105zanych danych z tabel HistoriaOdtworzen'),
'    DELETE FROM HistoriaOdtworzen',
'    WHERE id_uzytkownik = id_uzytkownikU;',
'',
unistr('    -- Usuni\0119cie powi\0105zanych danych z tabel Subskrypcje'),
'    DELETE FROM Uzytkownik_Plan_Subskrypcyjny',
'    WHERE UZYTKOWNIK_id_uzytkownik = id_uzytkownikU;',
'',
unistr('    -- Usuni\0119cie powi\0105zanych danych z tabel Platnosci'),
'    DELETE FROM Platnosc',
'    WHERE UZYTKOWNIK_id_uzytkownik = id_uzytkownikU;',
'',
'    DELETE FROM Likes',
'	where uzytkownik_id_uzytkownik = id_uzytkownikU;',
'',
'    DELETE FROM LISTY_ODTWARZANIA',
'    WHERE UZYTKOWNIK_ID_UZYTKOWNIK = ID_uzytkownikU;',
'',
'    DELETE FROM OBSERWUJACY',
'    WHERE UZYTKOWNIK_ID_UZYTKOWNIK =ID_UZYTKOWNIKU;',
'',
'    DELETE FROM UZYTKOWNIK_PREMIUM',
'    WHERE UZYTKOWNIK_ID_UZYTKOWNIK = ID_UZYTKOWNIKU;',
unistr('    -- Usuni\0119cie rekordu u\017Cytkownika z tabeli Uzytkownik'),
'    DELETE FROM Uzytkownik',
'    WHERE id_uzytkownik = id_uzytkownikU;',
'',
'    -- Informacja o powodzeniu operacji',
unistr('    DBMS_OUTPUT.PUT_LINE(''U\017Cytkownik oraz wszystkie powi\0105zane dane zosta\0142y pomy\015Blnie usuni\0119te.'');'),
'EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
unistr('        -- Obs\0142uga sytuacji, gdy u\017Cytkownik nie istnieje'),
unistr('        DBMS_OUTPUT.PUT_LINE(''Nie znaleziono u\017Cytkownika o podanym ID.'');'),
'    WHEN OTHERS THEN',
unistr('        -- Obs\0142uga wszelkich innych b\0142\0119d\00F3w'),
unistr('        DBMS_OUTPUT.PUT_LINE(''Wyst\0105pi\0142 b\0142\0105d podczas usuwania u\017Cytkownika.'');'),
'END UsunUzytkownika;',
'',
'',
'  PROCEDURE ZaktualizujDaneUzytkownika(',
'      id_uzytkownikU IN INTEGER,',
'      nowe_imieU IN VARCHAR2 DEFAULT NULL,',
'      nowy_emailU IN VARCHAR2 DEFAULT NULL,',
'      nowy_typ_uzytkownikaU IN VARCHAR2 DEFAULT NULL',
'  ) IS',
'      brak_liter EXCEPTION;',
'      brak_symboli EXCEPTION;',
'      brak_liczb EXCEPTION;',
'      niedozwolony_typ_uzytkownika EXCEPTION;',
'      czy_premium BOOLEAN;',
'  BEGIN',
'      czy_premium := CzyUzytkownikPremium(id_uzytkownikU);',
'',
'      IF nowe_imieU IS NOT NULL AND (obsluga_liczby(nowe_imieU) OR obsluga_symbole(nowe_imieU)) THEN',
'          RAISE brak_liter;',
