unistr('            p_c004 => v_najczesciej_odtwarzany_utwor    -- Kolumna 4: Najcz\0119\015Bciej odtwarzany utw\00F3r'),
'        );',
'    END LOOP;',
'',
unistr('    -- Zamkni\0119cie kursora'),
'    CLOSE wynik;',
'',
unistr('    -- Komunikat do log\00F3w'),
unistr('    DBMS_OUTPUT.PUT_LINE(''Dane dla u\017Cytkownika o ID '' || id_uzytkownikU || '' zosta\0142y zapisane do kolekcji.'');'),
'EXCEPTION',
