'          AND UPS.STATUS = ''AKTYWNA'';',
'    v_email VARCHAR2(50);',
'    v_data_koniec DATE;',
'BEGIN',
'    OPEN uzytkownicy_cursor;',
'    LOOP',
'        FETCH uzytkownicy_cursor INTO v_email, v_data_koniec;',
'        EXIT WHEN uzytkownicy_cursor%NOTFOUND;',
'',
'        -- Logowanie do tabeli',
'        INSERT INTO LOG_PRZYPOMNIENIA (data_log, email, komunikat)',
'        VALUES (',
