'            v_email, ',
unistr('            ''Przypomnienie wys\0142ane. Data zako\0144czenia: '' || TO_CHAR(v_data_koniec, ''YYYY-MM-DD'')'),
'        );',
'',
unistr('        DBMS_OUTPUT.PUT_LINE(''Wysy\0142anie przypomnienia do: '' || v_email ||'),
unistr('                             ''. Data zako\0144czenia: '' || TO_CHAR(v_data_koniec, ''YYYY-MM-DD''));'),
'    END LOOP;',
'    CLOSE uzytkownicy_cursor;',
'END PrzypomnijOZakonczeniuSubskrypcji;',
'',
'END ZarzadzaniePlatnosciami;',
